import React, { useState } from 'react'
import "./Header.css"
import logo from "../../assets/logo.png"
import bars from"../../assets/bars.png"
import useWindowSize from '../../hooks/useWindowSize'
import { useEffect } from 'react'

function Header() {

    const[windowSize,setWindowSize]=useState([window.innerWidth,window.innerHeight])

    const [isMenuOpened,setIsMenuOpened]=useState(false)

    const [navClass,setNavClass]=useState("closed")

    const [burger_class,setBurgerClass]=useState(undefined)

    let screen;
    if(windowSize<=678){
        screen=true
    }
    else screen=false
 

    const handleClickBurger=()=>{
        if(burger_class===undefined){
            setBurgerClass("clicked")
            setIsMenuOpened(!isMenuOpened)
            setNavClass("opened")
        }
        
         if(burger_class==="clicked"){
            setBurgerClass("unclicked")
            setIsMenuOpened(!isMenuOpened)
            setNavClass("closed")     
            }
        
        if(burger_class==="unclicked"){
            setBurgerClass("clicked")
            setIsMenuOpened(!isMenuOpened)  
            setNavClass("opened")
      
        
        }         
    }

   
    useEffect(() => {
        const handleSize=()=>{
            setWindowSize([window.innerWidth,window.innerHeight])
            if(windowSize[0]>768){
                setNavClass("opened")
            }
            else{
                setNavClass("opened")

            }
    }    

    window.addEventListener("resize",handleSize)

  return () => {
    window.removeEventListener("resize",handleSize)
  }
    }, [])

    
    return(
    <div className='header'>
        <div className='logo'>
            <img src={logo}/>
        </div>

        {windowSize[0]<=768 &&
            <div className='burger-menu' onClick={handleClickBurger}>
                <div className={burger_class} ></div>
                <div className={burger_class} ></div>
                <div className={burger_class} ></div>
            </div>
            
        }
      

    </div>
    )
}

export default Header
