import React from 'react'
import { useState } from 'react'
import { testimonialsData } from "../../data/testimonialsData"
import rightArrow from "../../assets/rightArrow.png"
import leftArrow from "../../assets/leftArrow.png"
import "./Testimonials.css"

function Testimonials() {
    const [selected, setSelected] = useState(0);
    const [testClickRight, setTestClickRight] = useState(false);
    const [rightClass, setRightClass] = useState('');

    const handleLeftArrow = () => {
        // if(selected > 0){
        //     setSelected(prev => prev - 1);
        // }
        // if(selected === 0){
        //     setSelected(testimonialsData.length - 1);
        // }
    }

    const handleRightArrow = () => {
        if (selected >= 0) {
            setSelected(prev => (prev + 1) % testimonialsData.length);
        }
        setTestClickRight(!testClickRight);
        setRightClass(testClickRight ? '' : 'translateToRight');
    }

    return (
        <div className='testimonials'>
            <div className='testimonials-heading heading-section'>
                TESTIMONIALS
            </div>

            <div className='testimonials-container'>
                <div className='testimonials-left-side'>
                    <div className='heading-right'>What They Say About Us</div>
                    <div className={rightClass}>{testimonialsData[selected].review}</div>
                    <div className={`status ${testClickRight ? "disp" : ""}`}>
                        <span>{testimonialsData[selected].name} - </span>
                        <span>{testimonialsData[selected].status}</span>
                    </div>
                </div>

                <div className='testimonials-right-side'>
                    <div className='img-left-container'>
                        <div className='cadre'></div>
                        <div className='full'></div>
                        <img src={testimonialsData[selected].image} className={`${testClickRight ? "translateToLeft" : ""}`} />
                        <div className='arrows'>
                            <img src={leftArrow} onClick={handleLeftArrow} />
                            <img src={rightArrow} onClick={handleRightArrow} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Testimonials;
