import React, { useState } from 'react'
import './Plans.css'
import { plansData } from '../../data/plansData'
import tick from "../../assets/whiteTick.png"
import rightArrow from "../../assets/rightArrow.png"

function Plans() {

    const[selected,setSelected]=useState(false)
    const [indexSelected,setIndexSelected]=useState(null)
    const[classHovered,setClassHovered]=useState("")

    console.log(selected)
    console.log(indexSelected)
    console.log(classHovered)

    const handleHover=(test,index,e)=>{
      setIndexSelected(index)
      for(let i=0;i<3;i++){
        if(i!=index){
          setClassHovered("")
          console.log(`i ${i}`)
          console.log(`index${index}`)

        }
        else
        setClassHovered("plan-card-selected")

      }
      setSelected(true)
    
    }

    const handleLeave=(test,index,e)=>{
      setIndexSelected(null)
      setSelected(false)
      setClassHovered("")

    }





  return (
    <div className='plans'>
        <div className='blur plans-blur1'></div>
        <div className='blur plans-blur2'></div>

        <div className='heading-section'>
            <span className='stoke-text'>READY TO START </span>
            <span>YOUR JOURNEY</span>
            <span className='stoke-text'>NOW </span>
        </div>

        <div className={`plans-container`}>

            {plansData.map((elt,index)=>


              <div className={`plan-card ${classHovered}`} key={index}
               onMouseOver={(e) => handleHover(true, index,e)}
               onMouseOut={(e) => handleLeave(false, index,e)}>

                  {elt.icon}

                  <div className='name'>{elt.name}</div>
                  <div className='price'>$ {elt.price}</div>

                  <div className='features'>
                      {elt.features.map((text,i)=>
                        <div key={i}>
                          <img src={tick}/>
                          <span>{text}</span> 
                        </div>
                        )}
                  </div>

                  <div className='benefits'>
                        <span>See more benefits</span>
                        <img src={rightArrow}/>
                  </div>

                  <div className='join'>
                    <button>Join Now</button>
                  </div>
              </div>

            )}

        </div>
    
    
    </div>
  )
}

export default Plans
