import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import useAutoIncrement from '../../hooks/useAutoIncrement'


export default function AutoCounter() {

    const [counter1,setCounter1]=useState(0)
    
    const [counter2,setCounter2]=useState(0)

    const [counter3,setCounter3]=useState(0)

    // useAutoIncrement(140,counter1,1000,setCounter1)

    let endValue=parseInt(140)

    console.log(endValue)

    let duration=Math.floor(1000/endValue)

    console.log(duration)

    useEffect(() => {

        let counterInterval=setInterval(() => {

            setCounter1((prevC)=>prevC+1)
    
            if(counter===endValue){
                alert(`${counter} ${endValue}`)
                clearInterval(counterInterval)
            }

            return ()=>{clearInterval(counterInterval)}
            
        },duration);
    
    }, [])




  return (
    <div className='figures'>
        <div>
            <div>+ {counter1}</div>
            <span>EXPERT COACHES</span>
        </div>
    
        <div>
        <div>+ {counter2}</div>
        <span>MEMBERS JOINED</span>
        </div>
  
        <div>
        <div>+ {counter3}</div>
        <span>FITNESS PROGRAMS</span>
        </div>     
  
    </div>  
    
  )
}
